// API Error Handling and Retry Logic
const maxRetries = 3;
const retryDelay = 5000; // 5 seconds
const currentRetry = $node.context.get('retry_count') || 0;

// Get the last error
const lastError = $input.all()[0]?.error;
const errorMessage = lastError?.message || 'Unknown API error';

// Log error details
console.log(`API Error (Retry ${currentRetry}/${maxRetries}): ${errorMessage}`);

// Check if we should retry
if (currentRetry < maxRetries) {
  // Increment retry counter
  $node.context.set('retry_count', currentRetry + 1);

  // Wait before retry
  await new Promise(resolve => setTimeout(resolve, retryDelay * currentRetry));

  // Return error for retry
  throw new Error(`Retrying API call (${currentRetry + 1}/${maxRetries}): ${errorMessage}`);
} else {
  // Max retries exceeded, use fallback or fail gracefully
  console.log('Max retries exceeded, attempting fallback...');

  // Reset retry counter for next execution
  $node.context.set('retry_count', 0);

  // Return fallback data or error
  return [{
    json: {
      error: true,
      message: `API failed after ${maxRetries} retries: ${errorMessage}`,
      fallback_used: true,
      timestamp: new Date().toISOString()
    }
  }];
}

