        "jsCode": "// Trading Calculator Error Handling
const executionResult = $input.all()[0];
const stdout = executionResult?.json?.stdout || '';
const stderr = executionResult?.json?.stderr || '';
const exitCode = executionResult?.json?.exitCode;

// Check for execution errors
if (exitCode !== 0 || stderr.trim() !== '') {
  const errorMessage = stderr || `Calculator exited with code ${exitCode}`;
  console.error('Trading Calculator Error:', errorMessage);

  // Create error response
  return [{
    json: {
      error: true,
      error_type: 'calculator_execution',
      message: errorMessage,
      timestamp: new Date().toISOString(),
      // Fallback analysis with minimal data
      analysis_result: {
        symbol: 'UNKNOWN',
        error: true,
        trading_signals: {
          primary_direction: 'HOLD',
          confidence: 0.0,
          entry_levels: [],
          stop_loss: null,
          take_profit: [],
          risk_reward: 0
        }
      }
    }
  }];
}

// Try to parse stdout as JSON
try {
  const analysisResult = JSON.parse(stdout);

  // Validate required fields
  if (!analysisResult.trading_signals) {
    throw new Error('Missing trading_signals in calculator output');
  }

  return [{ json: analysisResult }];
} catch (parseError) {
  console.error('JSON Parse Error:', parseError.message);

  return [{
    json: {
      error: true,
      error_type: 'json_parse',
      message: `Failed to parse calculator output: ${parseError.message}`,
      raw_output: stdout,
      timestamp: new Date().toISOString()
    }
  }];
