// Data Transformation - API to Trading Calculator Format
const webhookData = $('Webhook Trigger').item.json;
const previousDayData = $('API Previous Day Data').item.json;
const currentPriceData = $('API Current Price').item.json;
const hourlyData = $('API Hourly Data').item.json;

// Helper function to calculate EMA
function calculateEMA(prices, period) {
  const multiplier = 2 / (period + 1);
  let ema = prices[0];

  for (let i = 1; i < prices.length; i++) {
    ema = (prices[i] * multiplier) + (ema * (1 - multiplier));
  }

  return ema;
}

// Helper function to calculate Bollinger Bands
function calculateBollingerBands(prices, period = 20, stdDev = 2) {
  const sma = prices.slice(-period).reduce((a, b) => a + b, 0) / period;
  const variance = prices.slice(-period).reduce((acc, price) => acc + Math.pow(price - sma, 2), 0) / period;
  const std = Math.sqrt(variance);

  return {
    upper: sma + (std * stdDev),
    lower: sma - (std * stdDev),
    middle: sma
  };
}

