// Get current day high/low from recent hourly data
const todayBars = hourlyData.results?.filter(bar => {
  const barDate = new Date(bar.t).toDateString();
  const today = new Date().toDateString();
  return barDate === today;
}) || [];

const dayHigh = Math.max(...todayBars.map(bar => bar.h));
const dayLow = Math.min(...todayBars.map(bar => bar.l));

// Calculate EMAs
const ema20 = hourlyPrices.length >= 20 ? calculateEMA(hourlyPrices.slice(-50), 20) : 0;
const ema50 = hourlyPrices.length >= 50 ? calculateEMA(hourlyPrices.slice(-100), 50) : 0;
const ema200 = hourlyPrices.length >= 200 ? calculateEMA(hourlyPrices, 200) : 0;

// Calculate Bollinger Bands for current and previous session
const currentBB = hourlyPrices.length >= 20 ? calculateBollingerBands(hourlyPrices.slice(-20)) : { upper: 0, lower: 0 };
const previousBB = hourlyPrices.length >= 40 ? calculateBollingerBands(hourlyPrices.slice(-40, -20)) : { upper: 0, lower: 0 };

// Get manual break points from webhook (if provided)
const manualBreakPoints = webhookData.manual_break_points || {
  "break_point": null,
  "max_pos_exp": null,
  "int_pos_exp": null,
  "int_neg_exp": null,
  "max_neg_exp": null
};

// Format data for trading calculator
const tradingData = {
  "symbol": symbol,
  "previous_day": {
    "high": previousDay.h || 0,
    "low": previousDay.l || 0,
    "close": previousDay.c || 0,
    "change": (previousDay.c - previousDay.o) || 0
  },
  "current_day": {
    "high": dayHigh || currentPrice * 1.01,
    "low": dayLow || currentPrice * 0.99,
    "current_price": currentPrice,
    "change": (currentPrice - previousDay.c) || 0
  },
  "manual_break_points": manualBreakPoints,
  "bollinger_bands": {
    "premarket_bbt_1h": currentBB.upper,
    "premarket_bbb_1h": currentBB.lower,
    "market_close_bbt_1h": previousBB.upper,
    "market_close_bbb_1h": previousBB.lower
  },
  "emas_1h": {
    "ema20": ema20,
    "ema50": ema50,
    "ema200": ema200
  }
};

return [{ json: tradingData }];

// Parse Trading Calculator Output
