const stdout = executionData.stdout;
const stderr = executionData.stderr;

// Check for errors
if (stderr && stderr.trim() !== '') {
  throw new Error(`Trading Calculator Error: ${stderr}`);
}

// Parse JSON output
let analysisResult;
try {
  analysisResult = JSON.parse(stdout);
} catch (error) {
  throw new Error(`Failed to parse calculator output: ${error.message}`);
}

// Validate required fields
if (!analysisResult.trading_signals) {
  throw new Error('Invalid calculator output: missing trading_signals');
}

return [{ json: analysisResult }];

